import { useState } from "react";

function Botoes () {

    let contador = 0;

    function contadorNormal(){
        contador ++;
    }

    const [contadorState, setContadorState] = useState(0);

    function contarState(){
        setContadorState(contadorState + 1);
    }

    return (
    <div className="container row">
        <div className="col">
            <h1>Hello World!</h1>
        </div>
        <div className="col">
            <p>Contador normal: {contador}</p>
            <button type="button" class="btn btn-danger" onClick={contadorNormal}>Contador normal</button>
        </div>
        <div className="col">
            <p>Contador com state: {contadorState}</p>
            <button type="button" class="btn btn-success" onClick={contarState}>Contador com state</button>
        </div>
    </div>
    );
}

export default Botoes;
