import { useState } from "react";

function Botoes () {

    let contador = 0;

    function contadorNormal(){
        contador ++;
    }

    const [contadorState, setContadorState] = useState(0);

    const [title, setTitle] = useState("Follow");

    function changeTitle(){
        if ({title} == "Follow") {
            setTitle("Unfollow");
        } else {
            setTitle("Follow")
        }
    }

    function contarState(){
        setContadorState(contadorState + 1);
    }

    return (
    <div className="container row">
        <div className="col">
            <p>Contador com state: {contadorState}</p>
            <button type="button" class="btn btn-success" onClick={setTitle("Unfollow")}>{title}</button>
        </div>
    </div>
    );
}

export default Botoes;
